import React, { Component } from 'react';
import frame from './frame.svg';
import './App.css';

class App extends Component {

  state = {
    link: '',
    isMouseDown: false,
    firstCoor: {
      x: 0,
      y: 0
    },
    imgCoor: {
      x: 178,
      y: 178
    },
    sidesImg: {
      maxX: '',
      maxY: '',
      width: 228,
      height: 228
    }
  }

  addLink = (e) => {
    if(e.keyCode === 13) {
      this.setState({link: e.target.value})
      console.log(this.state)
    }
  }

  isMouseDown = (e) => {    
    const firstCoor = {
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    }
    console.log(firstCoor)
    this.setState({ isMouseDown: true, firstCoor: {...firstCoor} })
  }

  isMouseUp = () => {
    this.setState({ isMouseDown: false })
    console.log("ЭЭЭЭЭЭЭЭЭЭ")
  }

  swipeImg = (e) => {
    if (this.state.isMouseDown) {
      const swipeCoor = {
        x: this.state.firstCoor.x - e.nativeEvent.offsetX,
        y: this.state.firstCoor.y - e.nativeEvent.offsetY
      }
      const imgCoor = {
        x: this.state.imgCoor.x + swipeCoor.x,
        y: this.state.imgCoor.y + swipeCoor.y
      }
      this.setState({imgCoor: {...imgCoor}})
    }
  }

  componentDidMount() {
    const width = document.querySelector('.fakeImg').width
    const height = document.querySelector('.fakeImg').heigth
    if (width >= heigth) {
      this.setState({sidesImg: { heigth: 228, width: "100%" }})
    } else if (width1 <= height) {
      this.setState({sidesImg: { heigth: "100%", width: 228 }})
    } else {
      this.setState({sidesImg: { heigth: 228, width: 228 }})
    }
  }

  component

  render() {

    return (
      <div className="App">
        <img className="fakeImg" src={this.state.link} hidden/>
        <div><input className="input-link" onKeyDown={this.addLink}></input></div>
        <div>
        <svg width="584" height="584" 
        viewportFill="white">
          <defs>
            <clipPath id="image-clip-path">
              <rect x="178" y="178" width="228" height="228"/>
            </clipPath>
          </defs>

          <image 
            onMouseDown={this.isMouseDown} 
            onMouseUp={this.isMouseUp} 
            onMouseMove={this.swipeImg} 
            clip-path="url(#image-clip-path)" 
            id="image" 
            x={this.state.imgCoor.x} 
            y={this.state.imgCoor.y} 
            width={this.state.sidesImg.width} 
            height={this.state.sidesImg.height} 
            xlinkHref="https://images.unsplash.com/photo-1550854108-80eedf24f398?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
            // preserveAspectRatio="xMidYMid slice"
          />


        </svg>
        </div>
      </div>
    );
  }
}

export default App;
