import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {
    link: '',
    isMouseDown: false,
    firstCoor: {
      x: 0,
      y: 0
    },
    imgCoor: {
      x: 178,
      y: 0
    },
    sidesImg: {
      maxX: '',
      minX: '',
      maxY: '',
      minY: '',
      width: 228,
      height: 228
    } 
  }

  addLink = (e) => {
    if(e.keyCode === 13) {
      this.setState({link: e.target.value})
    }
  }

  isMouseDown = (e) => {    
    const firstCoor = {
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    }
    this.setState({ isMouseDown: true, firstCoor: {...firstCoor} })
  }

  isMouseUp = () => {  
    this.setState({ isMouseDown: false })
  }

  swipeImg = (e) => {
    if (this.state.isMouseDown) {
      const swipeCoor = {
        x: this.state.firstCoor.x - e.nativeEvent.offsetX,
        y: this.state.firstCoor.y - e.nativeEvent.offsetY
      }
      const imgCoor = {
        x: this.state.imgCoor.x + (swipeCoor.x)/10,
        y: this.state.imgCoor.y + (swipeCoor.y)/10
      }
      const width = document.querySelector('.fakeImg').width
      const height = document.querySelector('.fakeImg').height
      // console.log(e.nativeEvent.offsetY)
      // console.log(width, height)
      if (width >= height) {
        // if(this.state.sidesImg.maxY - this.state.imgCoor.y, -this.state.sidesImg.minY + this.state.imgCoor.y) {
        this.setState({imgCoor: {x : imgCoor.x, y : 178}})
        // }
      } else {
        if((this.state.sidesImg.maxY - this.state.imgCoor.y >= 0) && (-this.state.sidesImg.minY + this.state.imgCoor.y >= 0)) {
          this.setState({imgCoor: {y : imgCoor.y, x: 178}})
      console.log(this.state.sidesImg.maxY - this.state.imgCoor.y, -this.state.sidesImg.minY + this.state.imgCoor.y)

        } else {
          this.setState({imgCoor: { imgCoor}})
          console.log("stop")
        }
      }
      // this.setState({imgCoor: {y : imgCoor.y}})
    }
  }

  componentDidUpdate(prevProps, prevState) {
    // console.log(prevState.link)
    if (prevState.link ==! this.state.link) {
      const width = document.querySelector('.fakeImg').width
      const height = document.querySelector('.fakeImg').height    
      if (width >= height) {
        // console.log('dfsfds')
        const count = (228*width)/height 
        const maxX = Math.round((count - 228)/2)
        const minX = Math.round((-count + 228)/2) 
        this.setState({sidesImg: { height: 228, width: "100%", maxX: maxX, minX: maxX }, imgCoor: {x: 0, y: 178}})

      } else if (width <= height) {
        // console.log('asaasd')
        const count = (228*height)/width
        // console.log(width, height, count) 
        const maxY = Math.round((count - 228)/2)
        const minY = Math.round((-count + 228)/2) 

        // console.log(maxY, minY)
        this.setState({sidesImg: { height: "100%", width: 228, maxY: maxY, minY: minY }, imgCoor: {x: 178, y: 0}})
      } else {
        this.setState({sidesImg: { height: 228, width: 228 }})
      }

      // if (this.state.sidesImg.height === 228) {
      //   const firstCoor = {
      //     x: width / count,
      //     y: this.state.sidesImg.height / 2
      //   }
      //   this.setState({firstCoor: {...firstCoor}})
      // } else if (this.state.sidesImg.width === 228) {
      //   const firstCoor = {
      //     x: this.state.sidesImg.width / 2,
      //     y: height / count
      //   }
      //   this.setState({firstCoor: {...firstCoor}})
      // }
    }
  }

  render() {
    // console.log(this.state)
    return (
      <div className="App">
        <img className="fakeImg" src={this.state.link} hidden/>
        <div><input className="input-link" onKeyDown={this.addLink}></input></div>
        <div>
        <svg width="584" height="584">
          <defs>
            <clipPath id="image-clip-path">
              <rect x="178" y="178" width="228" height="228"/>
            </clipPath>
          </defs>

          <image 
            onMouseDown={this.isMouseDown} 
            onMouseUp={this.isMouseUp} 
            onMouseMove={this.swipeImg} 
            onMouseOut={this.isMouseUp}
            clipPath="url(#image-clip-path)" 
            id="image" 
            x={this.state.imgCoor.x} 
            y={this.state.imgCoor.y} 
            // x = "-178"
            // y = "178"
            width={this.state.sidesImg.width} 
            // width="100%"
            height={this.state.sidesImg.height}
            // height="228"
            // xlinkHref="https://images.unsplash.com/photo-1550854108-80eedf24f398?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" 
            xlinkHref={this.state.link}
            // preserveAspectRatio="xMidYMid slice"
          />


        </svg>
        </div>
      </div>
    );
  }
}

export default App;
