import React, { Component } from 'react';
import frame from './frame.svg';
import './App.css';

class App extends Component {

  state = {
    link: '',
    isMouseDown: false,
    firstCoor: {
      x: 0,
      y: 0
    },
    imgCoor: {
      x: 178,
      y: 178
    }
  }

  addLink = (e) => {
    if(e.keyCode === 13) {
      this.setState({link: e.target.value})
      console.log(this.state)
    }
  }

  isMouseDown = (e) => {    
    const firstCoor = {
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    }
    console.log(firstCoor)
    this.setState({ isMouseDown: true, firstCoor: {x: firstCoor.x, y: firstCoor.y} })
  }

  isMouseUp = () => {
    this.setState({ isMouseDown: false })
  }

  swipeImg = (e) => {
    if (this.state.isMouseDown) {
      const swipeCoor = {
        x: firstCoor.x - e.nativeEvent.offsetX,
        y: firstCoor.y - e.nativeEvent.offsetY
      }
      this.setState({imgCoor: {...swipeCoor}})
    }
  }

  render() {

    return (
      <div className="App">
        <div><input className="input-link" onKeyDown={this.addLink}></input></div>
        <div >
        <svg width="584" height="584" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" viewportFill="white">
          <defs>
            <clipPath id="image-clip-path">
              <rect x={this.state.imgCoor.x} y={this.state.imgCoor.y} width="228" height="228"/>
            </clipPath>
          </defs>

          <image onMouseDown={this.isMouseDown} onMouseUp={this.isMouseUp} onMouseMove={this.swipeImg} clip-path="url(#image-clip-path)" id="image" x="178" y="178" width="228" height="228" 
            xlinkHref={this.state.link} preserveAspectRatio="xMidYMid slice"/>


        </svg>
        </div>
      </div>
    );
  }
}

export default App;
