import React, { Component } from 'react';
import frame from './frame.svg';
import './App.css';

class App extends Component {

  state = {
    link: '',
    isMouseDown: false,
    firstCoor: {
      x: 0,
      y: 0
    },
    imgCoor: {
      x: 178,
      y: 178
    },
    sidesImg: {
      maxX: '',
      maxY: '',
      width: 228,
      height: 228
    }
  }

  addLink = (e) => {
    if(e.keyCode === 13) {
      this.setState({link: e.target.value})
    }
  }

  isMouseDown = (e) => {    
    const firstCoor = {
      x: e.nativeEvent.offsetX,
      y: e.nativeEvent.offsetY
    }
    this.setState({ isMouseDown: true, firstCoor: {...firstCoor} })
  }

  isMouseUp = () => {
    this.setState({ isMouseDown: false })
  }

  swipeImg = (e) => {
    if (this.state.isMouseDown) {
      const swipeCoor = {
        x: this.state.firstCoor.x - e.nativeEvent.offsetX,
        y: this.state.firstCoor.y - e.nativeEvent.offsetY
      }
      const imgCoor = {
        x: this.state.imgCoor.x + swipeCoor.x,
        y: this.state.imgCoor.y + swipeCoor.y
      }
      const width = document.querySelector('.fakeImg').width
      const height = document.querySelector('.fakeImg').height
      console.log(width, height)
      if (width >= height) {
        this.setState({imgCoor: {x : imgCoor.x}})
      } else {
        this.setState({imgCoor: {x : imgCoor.y}})
      }
    }
  }

  componentDidUpdate(prevProps, prevState) {
    console.log(prevState.link)
    if (prevState.link ==! this.state.link) {
      const width = document.querySelector('.fakeImg').width
      const height = document.querySelector('.fakeImg').height
      console.log(width, height)
      if (width >= height) {
        console.log('dfsfds')
        this.setState({sidesImg: { height: "228", width: "100%" }})
      } else if (width <= height) {
        this.setState({sidesImg: { height: "100%", width: 228 }})
      } else {
        this.setState({sidesImg: { height: 228, width: 228 }})
      }
    }
  }

  render() {
    console.log(this.state)
    return (
      <div className="App">
        <img className="fakeImg" src={this.state.link} hidden/>
        <div><input className="input-link" onKeyDown={this.addLink}></input></div>
        <div>
        <svg width="584" height="584">
          <defs>
            <clipPath id="image-clip-path">
              <rect x="178" y="178" width="228" height="228"/>
            </clipPath>
          </defs>

          <image 
            onMouseDown={this.isMouseDown} 
            onMouseUp={this.isMouseUp} 
            onMouseMove={this.swipeImg} 
            clipPath="url(#image-clip-path)" 
            id="image" 
            x={this.state.imgCoor.x} 
            y={this.state.imgCoor.y} 
            width={this.state.sidesImg.width} 
            // width="100%"
            height={this.state.sidesImg.height}
            // height="228"
            // xlinkHref="https://images.unsplash.com/photo-1550854108-80eedf24f398?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" 
            xlinkHref={this.state.link}
            // preserveAspectRatio="xMidYMid slice"
          />


        </svg>
        </div>
      </div>
    );
  }
}

export default App;
